import { Link, useLocation } from "react-router-dom";

const navItems = [
  { name: "Home", path: "/" },
  { name: "About", path: "/about" },
  { name: "Education", path: "/education" },
  { name: "Working Experience", path: "/working-experience" },
  { name: "Licenses & Certifications", path: "/licenses-and-certifications" },
  { name: "Organization", path: "/organization" },
  { name: "Skills", path: "/skills" },
  { name: "Honorary Titles & Awards", path: "/honorary-titles-and-awards" },
  { name: "Technologies", path: "/technologies" },
  { name: "Contact", path: "/contact" },
];

export default function Navbar() {
  const location = useLocation();

  return (
    <nav className="bg-white shadow-sm border-b border-blue-100 sticky top-0 z-50 overflow-x-auto">
      <div className="max-w-none min-w-fit px-6 py-3 flex items-center gap-6">
        {/* Logo */}
        <div className="text-base sm:text-lg md:text-xl font-bold text-blue-600 whitespace-nowrap">
          Akhdan Profile
        </div>

        {/* Nav Links - sejajar horizontal scrollable if overflow */}
        <ul className="flex items-center gap-4 text-sm sm:text-base font-medium text-gray-700 whitespace-nowrap">
          {navItems.map((item) => {
            const isActive = location.pathname === item.path;
            return (
              <li key={item.name}>
                <Link
                  to={item.path}
                  className={`px-2 py-1 transition-colors duration-200 hover:text-blue-600 ${
                    isActive
                      ? "text-blue-600 font-semibold border-b-2 border-blue-500"
                      : ""
                  }`}
                >
                  {item.name}
                </Link>
              </li>
            );
          })}
        </ul>
      </div>
    </nav>
  );
}
