import { Link, useLocation } from "react-router-dom";

const navItems = [
  { name: "Home", path: "/" },
  { name: "About", path: "/about" },
  { name: "Education", path: "/education" },
  { name: "Working Experience", path: "/working-experience" },
  { name: "Licenses & Certifications", path: "/licenses-and-certifications" },
  { name: "Organization", path: "/organization" },
  { name: "Skills", path: "/skills" },
  { name: "Honorary Titles & Awards", path: "/honorary-titles-and-awards" },
  { name: "Technologies", path: "/technologies" },
  { name: "Project", path: "/project" },
  { name: "Contact", path: "/contact" },
];

export default function Navbar() {
  const location = useLocation();

  // Bagi navItems jadi dua grup, misalnya 6 dan 5 item
  const firstRow = navItems.slice(0, 6);
  const secondRow = navItems.slice(6);

  return (
    <nav className="bg-white shadow-sm border-b border-blue-100 sticky top-0 z-50 w-full">
      <div className="px-4 sm:px-6 py-3 space-y-2">
        {/* Logo */}
        <div className="text-lg sm:text-xl font-bold text-blue-600 whitespace-nowrap mb-1">
          Akhdan Profile
        </div>

        {/* Baris 1 */}
        <ul className="flex flex-wrap gap-4 text-sm sm:text-base font-medium text-gray-700">
          {firstRow.map((item) => {
            const isActive = location.pathname === item.path;
            return (
              <li key={item.name}>
                <Link
                  to={item.path}
                  className={`transition-colors duration-200 hover:text-blue-600 ${
                    isActive
                      ? "text-blue-600 font-semibold border-b-2 border-blue-500"
                      : ""
                  }`}
                >
                  {item.name}
                </Link>
              </li>
            );
          })}
        </ul>

        {/* Baris 2 */}
        <ul className="flex flex-wrap gap-4 text-sm sm:text-base font-medium text-gray-700">
          {secondRow.map((item) => {
            const isActive = location.pathname === item.path;
            return (
              <li key={item.name}>
                <Link
                  to={item.path}
                  className={`transition-colors duration-200 hover:text-blue-600 ${
                    isActive
                      ? "text-blue-600 font-semibold border-b-2 border-blue-500"
                      : ""
                  }`}
                >
                  {item.name}
                </Link>
              </li>
            );
          })}
        </ul>
      </div>
    </nav>
  );
}
