import { motion } from "framer-motion";
import { useLocation } from "react-router-dom";

export default function Section({ title }) {
  const location = useLocation();

  const isHome = location.pathname === "/";

  if (isHome) {
    return (
      <motion.section
        className="p-8 flex flex-col-reverse md:flex-row items-center justify-between gap-8 max-w-6xl mx-auto"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        <div className="flex-1">
  <h1 className="text-4xl font-bold text-gray-800 mb-4">
    Halo, saya <span className="text-blue-500">Raka</span>
  </h1>
  <p className="text-lg text-gray-600 mb-6">
    Saya adalah seorang Web Developer yang passionate membangun aplikasi web yang elegan, efisien, dan modern.
  </p>
  <a
    href="/about"
    className="inline-block bg-blue-500 hover:bg-blue-600 text-white font-semibold px-6 py-3 rounded-xl transition"
  >
    Get Started
  </a>
</div>
        <motion.div
          className="w-40 h-40 md:w-60 md:h-60 rounded-full overflow-hidden shadow-lg border-4 border-blue-400"
          initial={{ scale: 0.8 }}
          animate={{ scale: 1 }}
          transition={{ duration: 0.5 }}
        >
          <img
            src="/profil.jpg"
            alt="Raka"
            className="object-cover w-full h-full"
          />
        </motion.div>
      </motion.section>
    );
  }

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">{title}</h1>
      <p className="text-gray-700">Konten untuk {title} akan ditampilkan di sini.</p>
    </div>
  );
}
