import { Link, useLocation } from "react-router-dom";

const navItems = [
  { name: "Home", path: "/" },
  { name: "About", path: "/about" },
  { name: "Education", path: "/education" },
  { name: "Working Experience", path: "/working-experience" },
  { name: "Licenses & Certifications", path: "/licenses-and-certifications" },
  { name: "Organization", path: "/organization" },
  { name: "Skills", path: "/skills" },
  { name: "Honorary Titles & Awards", path: "/honorary-titles-and-awards" },
  { name: "Technologies", path: "/technologies" },
  { name: "Contact", path: "/contact" },
];

export default function Navbar() {
  const location = useLocation();

  return (
    <nav className="bg-white shadow-md sticky top-0 z-50 border-b border-blue-100">
      <div className="max-w-7xl mx-auto px-6 sm:px-8 lg:px-12">
        <div className="flex flex-col sm:flex-row items-center justify-between h-auto py-4 gap-4 sm:gap-0">
          {/* Logo */}
          <div className="text-2xl font-bold text-blue-600 tracking-wide">
            Akhdan Profile
          </div>

          {/* Navigation Links */}
          <ul className="flex flex-wrap justify-center sm:justify-end items-center gap-4 text-sm sm:text-base text-gray-700 font-medium">
            {navItems.map((item) => {
              const isActive = location.pathname === item.path;
              return (
                <li key={item.name}>
                  <Link
                    to={item.path}
                    className={`relative transition-all duration-200 hover:text-blue-600 ${
                      isActive ? "text-blue-600 font-semibold" : ""
                    }`}
                  >
                    {item.name}
                    {isActive && (
                      <span className="absolute left-0 -bottom-1 w-full h-[2px] bg-blue-500 rounded-md"></span>
                    )}
                  </Link>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    </nav>
  );
}
