import { motion } from "framer-motion";
import { useLocation } from "react-router-dom";

export default function Section({ title }) {
  const location = useLocation();
  const isHome = location.pathname === "/";

  if (isHome) {
    return (
      <motion.section
        className="relative min-h-screen bg-gradient-to-br from-blue-50 via-white to-blue-100 flex items-center justify-center px-6 sm:px-12 py-16 overflow-hidden"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.6 }}
      >
        {/* Background SVG Decorative */}
        <div className="absolute top-0 left-0 w-full h-full pointer-events-none">
          <svg
            className="absolute -top-20 -left-20 w-96 opacity-10"
            viewBox="0 0 200 200"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              fill="#3b82f6"
              d="M47.8,-58.7C62.1,-48.9,73.9,-31.1,75.3,-13.3C76.7,4.4,67.7,22.1,56.2,35.6C44.8,49.1,30.9,58.5,15.6,65C0.3,71.5,-16.3,75.1,-31.5,69.3C-46.7,63.5,-60.5,48.3,-66.6,31.3C-72.8,14.3,-71.2,-4.6,-63.1,-20.5C-54.9,-36.3,-40.1,-49.1,-24.4,-58.6C-8.6,-68,7.9,-74.2,24.7,-71.3C41.4,-68.4,58.4,-56.6,47.8,-58.7Z"
              transform="translate(100 100)"
            />
          </svg>
        </div>

        <div className="flex flex-col-reverse md:flex-row items-center justify-between w-full max-w-6xl gap-12 relative z-10">
          {/* Text */}
          <motion.div
            className="flex-1 text-center md:text-left"
            initial={{ x: -50, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ duration: 0.8 }}
          >
            <h1 className="text-4xl sm:text-5xl font-bold text-gray-800 leading-tight mb-4">
              Halo, saya <span className="text-blue-600">Raka</span>
            </h1>
            <p className="text-gray-600 text-base sm:text-lg mb-6 max-w-lg mx-auto md:mx-0">
              Seorang Web Developer yang bersemangat membangun aplikasi web modern, efisien, dan elegan.
            </p>
            <a
              href="/about"
              className="inline-block bg-blue-600 hover:bg-blue-700 text-white text-sm sm:text-base font-medium px-6 py-3 rounded-xl shadow-md transition transform hover:scale-105"
            >
              Get Started â†’
            </a>
          </motion.div>

          {/* Gambar Profil dengan animasi floating */}
          <motion.div
            className="w-36 h-36 sm:w-48 sm:h-48 md:w-64 md:h-64 rounded-full overflow-hidden border-4 border-blue-500 shadow-xl bg-white"
            initial={{ y: 0 }}
            animate={{
              y: [0, -10, 0], // naik turun perlahan
            }}
            transition={{
              repeat: Infinity,
              duration: 3,
              ease: "easeInOut",
            }}
          >
            <img
              src="/profil.jpg"
              alt="Foto Raka"
              className="w-full h-full object-cover"
            />
          </motion.div>
        </div>
      </motion.section>
    );
  }

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">{title}</h1>
      <p className="text-gray-700">Konten untuk {title} akan ditampilkan di sini.</p>
    </div>
  );
}
