import { motion } from "framer-motion";
import { useLocation } from "react-router-dom";

export default function Section({ title }) {
  const location = useLocation();
  const isHome = location.pathname === "/";

  if (isHome) {
    return (
      <motion.section
        className="min-h-screen bg-gradient-to-br from-white via-blue-50 to-blue-100 p-6 sm:p-8 flex flex-col-reverse md:flex-row items-center justify-center gap-12 max-w-6xl mx-auto"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.6 }}
      >
        {/* Kiri: Teks */}
        <motion.div
          className="flex-1 text-center md:text-left"
          initial={{ x: -30, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ duration: 0.7 }}
        >
          <h1 className="text-3xl sm:text-4xl md:text-5xl font-extrabold text-gray-800 mb-4 leading-snug">
            Halo, saya <span className="text-blue-600">Raka</span>
          </h1>
          <p className="text-base sm:text-lg text-gray-700 mb-6 max-w-md mx-auto md:mx-0">
            Saya adalah Web Developer yang passionate membangun aplikasi web yang elegan, efisien, dan modern.
          </p>
          <a
            href="/about"
            className="inline-block w-full sm:w-auto bg-blue-600 hover:bg-blue-700 text-white font-semibold px-6 py-3 rounded-xl shadow-md hover:shadow-xl transition transform hover:scale-105"
          >
            Get Started
          </a>
        </motion.div>

        {/* Kanan: Gambar */}
        <motion.div
          className="w-32 h-32 sm:w-40 sm:h-40 md:w-60 md:h-60 rounded-full overflow-hidden shadow-2xl border-4 border-blue-400"
          initial={{ x: 30, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ duration: 0.7 }}
        >
          <img
            src="/profil.jpg"
            alt="Raka"
            className="object-cover w-full h-full"
          />
        </motion.div>
      </motion.section>
    );
  }

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">{title}</h1>
      <p className="text-gray-700">Konten untuk {title} akan ditampilkan di sini.</p>
    </div>
  );
}
