import { motion } from "framer-motion";
import { useLocation } from "react-router-dom";

const sectionContent = {
  "/about": {
    title: "About",
    description:
      "Saya Akhdan Shalahudin, seorang Frontend Developer yang antusias dalam membangun UI modern, interaktif, dan efisien menggunakan React dan Tailwind CSS.",
  },
  "/education": {
    title: "Education",
    description: "Berikut adalah latar belakang pendidikan saya.",
  },
  "/working-experience": {
    title: "Working Experience",
    description: "Pengalaman kerja saya di berbagai perusahaan dan proyek.",
  },
  "/licenses-and-certifications": {
    title: "Licenses & Certifications",
    description: "Sertifikasi dan lisensi profesional yang telah saya raih.",
  },
  "/organization": {
    title: "Organization",
    description: "Kegiatan dan posisi organisasi yang pernah saya ikuti.",
  },
  "/skills": {
    title: "Skills",
    description: "Keahlian teknis dan soft skill yang saya kuasai.",
  },
  "/honorary-titles-and-awards": {
    title: "Honorary Titles & Awards",
    description: "Penghargaan dan gelar kehormatan yang saya dapatkan.",
  },
  "/technologies": {
    title: "Technologies",
    description: "Teknologi dan tools yang saya gunakan secara profesional.",
  },
  "/contact": {
    title: "Contact",
    description: "Hubungi saya melalui email atau media sosial.",
  },
};

export default function Section({ title }) {
  const location = useLocation();
  const isHome = location.pathname === "/";
  const content = sectionContent[location.pathname];

  if (isHome) {
    return (
      <motion.section
        className="relative min-h-screen bg-gradient-to-br from-blue-50 via-white to-blue-100 flex items-center justify-center px-6 sm:px-12 py-20 overflow-hidden"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.6 }}
      >
        {/* SVG Background */}
        <div className="absolute top-0 left-0 w-full h-full pointer-events-none z-0">
          {/* SVGs omitted for brevity */}
        </div>

        {/* Content */}
        <div className="flex flex-col-reverse md:flex-row items-center justify-between w-full max-w-6xl gap-12 z-10">
          <motion.div
            className="flex-1 text-center md:text-left"
            initial={{ x: -60, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ duration: 0.8 }}
          >
            <p className="text-sm sm:text-base text-blue-500 mb-2 font-medium tracking-wide">
              Web Developer â€¢ Front-End Enthusiast
            </p>
            <h1 className="text-4xl sm:text-5xl font-bold text-gray-800 leading-tight mb-4">
              Halo, saya{" "}
              <span className="text-blue-600 border-b-4 border-blue-300 hover:text-blue-700 transition-colors cursor-pointer">
                Akhdan Shalahudin
              </span>
            </h1>
            <p className="text-gray-600 text-base sm:text-lg mb-6 max-w-lg mx-auto md:mx-0">
              Frontend Developer yang berfokus pada pembuatan antarmuka modern, responsif, dan interaktif menggunakan React dan Tailwind CSS.
            </p>
            <a
              href="/about"
              className="inline-flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white text-sm sm:text-base font-semibold px-6 py-3 rounded-xl shadow-lg transition transform hover:scale-105"
            >
              Get Started
              <svg className="w-4 h-4" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
                <path d="M5 12h14M12 5l7 7-7 7" />
              </svg>
            </a>
          </motion.div>

          <motion.div
            className="w-36 h-36 sm:w-48 sm:h-48 md:w-64 md:h-64 rounded-full overflow-hidden border-4 border-blue-500 shadow-2xl bg-white"
            initial={{ y: 0 }}
            animate={{ y: [0, -12, 0] }}
            transition={{
              repeat: Infinity,
              duration: 3,
              ease: "easeInOut",
            }}
          >
            <img
              src="/profil.jpg"
              alt="Foto Akhdan"
              className="w-full h-full object-cover transition-transform duration-500 hover:scale-105"
            />
          </motion.div>
        </div>
      </motion.section>
    );
  }

  return (
    <section className="py-20 px-6 sm:px-12 max-w-4xl mx-auto">
      <motion.div
        className="bg-white p-6 sm:p-10 rounded-xl shadow-md border border-blue-100"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h1 className="text-3xl font-bold text-blue-700 mb-4">
          {content?.title || "Page Not Found"}
        </h1>
        <p className="text-gray-700 text-base leading-relaxed">
          {content?.description || "Maaf, halaman ini tidak tersedia."}
        </p>
      </motion.div>
    </section>
  );
}
