import { Link, useLocation } from "react-router-dom";

const navItems = [
  { name: "Home", path: "/" },
  { name: "About", path: "/about" },
  { name: "Education", path: "/education" },
  { name: "Working Experience", path: "/working-experience" },
  { name: "Licenses & Certifications", path: "/licenses-and-certifications" },
  { name: "Organization", path: "/organization" },
  { name: "Skills", path: "/skills" },
  { name: "Honorary Titles & Awards", path: "/honorary-titles-and-awards" },
  { name: "Technologies", path: "/technologies" },
  { name: "Project", path: "/project" },
  { name: "Contact", path: "/contact" },
];

export default function Navbar() {
  const location = useLocation();

  // Bagi navItems menjadi dua bagian
  const topNavItems = navItems.slice(0, 6); // tampil di baris atas
  const bottomNavItems = navItems.slice(6); // tampil di baris bawah

  return (
    <nav className="bg-white shadow-sm border-b border-blue-100 sticky top-0 z-50 w-full">
      <div className="px-4 sm:px-6 py-3 space-y-2">
        {/* Logo dan baris atas */}
        <div className="flex items-center justify-between">
          <div className="text-lg sm:text-xl font-bold text-blue-600 whitespace-nowrap">
            Akhdan Profile
          </div>
          <ul className="flex items-center gap-4 sm:gap-6 text-sm sm:text-base font-medium text-gray-700 whitespace-nowrap overflow-x-auto">
            {topNavItems.map((item) => {
              const isActive = location.pathname === item.path;
              return (
                <li key={item.name}>
                  <Link
                    to={item.path}
                    className={`px-2 py-1 transition-colors duration-200 hover:text-blue-600 ${
                      isActive
                        ? "text-blue-600 font-semibold border-b-2 border-blue-500"
                        : ""
                    }`}
                  >
                    {item.name}
                  </Link>
                </li>
              );
            })}
          </ul>
        </div>

        {/* Baris bawah */}
        <div className="overflow-x-auto">
          <ul className="flex items-center gap-4 sm:gap-6 text-sm sm:text-base font-medium text-gray-700 whitespace-nowrap">
            {bottomNavItems.map((item) => {
              const isActive = location.pathname === item.path;
              return (
                <li key={item.name}>
                  <Link
                    to={item.path}
                    className={`px-2 py-1 transition-colors duration-200 hover:text-blue-600 ${
                      isActive
                        ? "text-blue-600 font-semibold border-b-2 border-blue-500"
                        : ""
                    }`}
                  >
                    {item.name}
                  </Link>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    </nav>
  );
}
