import { motion } from "framer-motion";
import { useLocation } from "react-router-dom";

export default function Section({ title }) {
  const location = useLocation();
  const isHome = location.pathname === "/";

  if (isHome) {
    return (
      <motion.section
        className="min-h-screen bg-gradient-to-br from-[#f8fbff] via-[#eef5ff] to-[#e2efff] flex items-center justify-center px-6 sm:px-12 py-16"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.6 }}
      >
        <div className="flex flex-col-reverse md:flex-row items-center justify-between w-full max-w-6xl gap-12">
          {/* Text */}
          <motion.div
            className="flex-1 text-center md:text-left"
            initial={{ x: -50, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ duration: 0.8 }}
          >
            <h1 className="text-4xl sm:text-5xl font-bold text-gray-800 leading-tight mb-4">
              Halo, saya <span className="text-blue-600">Raka</span>
            </h1>
            <p className="text-gray-600 text-base sm:text-lg mb-6 max-w-lg mx-auto md:mx-0">
              Seorang Web Developer yang bersemangat membangun aplikasi web modern, efisien, dan elegan.
            </p>
            <a
              href="/about"
              className="inline-block bg-blue-600 hover:bg-blue-700 text-white text-sm sm:text-base font-medium px-6 py-3 rounded-xl shadow-md transition transform hover:scale-105"
            >
              Get Started â†’
            </a>
          </motion.div>

          {/* Image */}
          <motion.div
            className="w-36 h-36 sm:w-48 sm:h-48 md:w-64 md:h-64 rounded-full overflow-hidden border-4 border-blue-500 shadow-lg bg-white"
            initial={{ x: 50, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ duration: 0.8 }}
          >
            <img
              src="/profil.jpg"
              alt="Foto Raka"
              className="w-full h-full object-cover transition-transform duration-500 hover:scale-105"
            />
          </motion.div>
        </div>
      </motion.section>
    );
  }

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">{title}</h1>
      <p className="text-gray-700">Konten untuk {title} akan ditampilkan di sini.</p>
    </div>
  );
}
