import { Link, useLocation } from "react-router-dom";

const navItems = [
  { name: "Home", path: "/" },
  { name: "About", path: "/about" },
  { name: "Education", path: "/education" },
  { name: "Working Experience", path: "/working-experience" },
  { name: "Licenses & Certifications", path: "/licenses-and-certifications" },
  { name: "Organization", path: "/organization" },
  { name: "Skills", path: "/skills" },
  { name: "Honorary Titles & Awards", path: "/honorary-titles-and-awards" },
  { name: "Technologies", path: "/technologies" },
  { name: "Project", path: "/project" },
  { name: "Contact", path: "/contact" },
];

export default function Navbar() {
  const location = useLocation();

  // Bagi 2 bagian nav
  const firstHalf = navItems.slice(0, Math.ceil(navItems.length / 2));
  const secondHalf = navItems.slice(Math.ceil(navItems.length / 2));

  return (
    <nav className="bg-white border-b border-blue-100 shadow-sm sticky top-0 z-50 w-full">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 py-3">
        {/* Logo */}
        <div className="text-xl font-bold text-blue-600 mb-4">
          Akhdan Profile
        </div>

        {/* Nav Links */}
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-y-3">
          {[firstHalf, secondHalf].map((section, i) => (
            <ul
              key={i}
              className={`flex flex-wrap justify-${
                i === 0 ? "start" : "end"
              } gap-x-6 gap-y-2 text-sm sm:text-base font-medium text-gray-700`}
            >
              {section.map((item) => {
                const isActive = location.pathname === item.path;
                return (
                  <li key={item.name}>
                    <Link
                      to={item.path}
                      className={`transition-colors duration-200 hover:text-blue-600 ${
                        isActive
                          ? "text-blue-600 font-semibold border-b-2 border-blue-500 pb-0.5"
                          : ""
                      }`}
                    >
                      {item.name}
                    </Link>
                  </li>
                );
              })}
            </ul>
          ))}
        </div>
      </div>
    </nav>
  );
}
